// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

library BitMask {

    uint constant internal ONE = uint256(1);
    uint constant internal ONES = ~uint256(0);

    /**
    * @dev Internal function to set 1 bit in specific `index`
    * @return Updated bitmask with modified bit at `index`
    */
    function setBit(uint256 self, uint8 index) internal pure returns (uint256) {
        return self | ONE << index;
    }

    /**
    * @dev Internal function to clear bit to 0 in specific `index`
    * @return Updated bitmask with modified bit at `index`
    */
    function clearBit(uint256 self, uint8 index) internal pure returns (uint256) {
        return self & ~(ONE << index);
    }

    /**
    * @dev Internal function to check bit at specific `index`
    * @return Returns TRUE if bit is '1', FALSE otherwise
    */
    function checkBit(uint256 self, uint8 index) internal pure returns (bool) {
        return (self & (uint256(1) << index)) > 0;
    }

    /**
    * @dev Internal function to apply NOT operator for a bit in specific `index`
    * If the original bit is '1' sets bit to '0'
    * If the original bit is '0' sets bit to '1'
    * @return Updated bitmask with modified bit at `index`
    */
    function toggleBit(uint256 self, uint8 index) internal pure returns (uint256) {
        return self ^ ONE << index;
    }

    /**
    * @dev AND operator between two bitmasks:
    * '0' AND '0' -> '0'
    * '0' AND '1' -> '0'
    * '1' AND '0' -> '0'
    * '1' AND '1' -> '1'
    */
    function and(uint256 self, uint256 mask) internal pure returns (uint256) {
        return self & mask;
    }

    /**
    * @dev OR operator between two bitmasks:
    * '0' OR '0' -> '0'
    * '0' OR '1' -> '1'
    * '1' OR '0' -> '1'
    * '1' OR '1' -> '1'
    */
    function or(uint256 self, uint256 mask) internal pure returns (uint256) {
        return self | mask;
    }

    /**
    * @dev XOR operator between two bitmasks:
    * '0' XOR '0' -> '0'
    * '0' XOR '1' -> '1'
    * '1' XOR '0' -> '1'
    * '1' XOR '1' -> '0'
    */
    function xor(uint256 self, uint256 mask) internal pure returns (uint256) {
        return self ^ mask;
    }

    /**
    * @dev NOT operator:
    * '0' NOT -> '1'
    * '1' NOT -> '0'
    */
    function negate(uint256 self) internal pure returns (uint256) {
        return self ^ ONES;
    }

    /**
    * @dev NOT operator:
    * '0' NOT -> '1'
    * '1' NOT -> '0'
    */
    function not(uint256 self) internal pure returns (uint256) {
        return ~self;
    }

    /**
    * @dev Left shift of `self` bitmask by `index`:
    */
    function shiftLeft(uint256 self, uint8 index) internal pure returns (uint256) {
        return self << index;
    }

    /**
    * @dev Left shift of `self` bitmask by `index`:
    */
    function shiftRight(uint256 self, uint8 index) internal pure returns (uint256) {
        return self >> index;
    }
}

contracts/TokenBitMask.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./library/BitMask.sol";
import "./interfaces/IFlagsStorage.sol";

/**
 * @dev TokenBitMask contract is an internal smart contract for Gateway Token
 * implementation that stores KYC flags per identity token in a bitmask
 */
contract TokenBitMask {
    using BitMask for uint256;

    /**
     * @dev Emitted when token BitMask associated with `tokenId` updated to `bitmask`.
     */
    event BitMaskUpdated(uint256 tokenId, uint256 bitmask);

    /**
     * @dev Emitted when Identity.com Admin updated FlagsStorage contract address from `previousFlagsStorage` to `flagsStorage`.
     */
    event FlagsStorageUpdated(address indexed flagsStorage);

    // Gateway Token system FlagsStorage contract address
    IFlagsStorage public flagsStorage;

    // Mapping for gateway token id to bitmask
    mapping(uint256 => uint256) private _bitmasks;

    /**
     * @dev Internal funciton to set FlagsStorage contract address
     * @param _flagsStorage FlagsStorage contract address
     */
    function _setFlagsStorage(address _flagsStorage) internal {
        flagsStorage = IFlagsStorage(_flagsStorage);

        emit FlagsStorageUpdated(_flagsStorage);
    }

    /**
     * @dev Internal function to get gateway token bitmask
     */
    function _getBitMask(uint256 _tokenId) internal view returns (uint256) {
        return _bitmasks[_tokenId];
    }

    /**
     * @dev Internal function to set full bitmask for gateway token
     * @notice This function rewrites previous bitmask, use _addBitmask if you need to add flags to existing bitmask
     */
    function _setBitMask(uint256 _tokenId, uint256 _mask) internal {
        _checkSupportedBits(_mask);
        _bitmasks[_tokenId] = _mask;

        emit BitMaskUpdated(_tokenId, _mask);
    }

    /**
     * @dev Internal function to add `_mask` to existing bitmask for gateway token with `_tokenId`
     * @notice This function performs validation on supported flags on the gateway token system level
     */
    function _addBitMask(uint256 _tokenId, uint256 _mask) internal {
        uint256 mask = _bitmasks[_tokenId];
        uint256 newMask = mask.or(_mask);
        _checkSupportedBits(newMask);

        _bitmasks[_tokenId] = newMask;

        emit BitMaskUpdated(_tokenId, newMask);
    }

    /**
     * @dev Internal function to add one bit at particular `_index` for gateway token with `_tokenId`
     * @notice This function performs validation on supported flags on the gateway token system level
     */
    function _addBit(uint256 _tokenId, uint8 _index) internal {
        uint256 mask = _bitmasks[_tokenId];
        uint256 newMask = mask.setBit(_index);
        _checkSupportedBits(newMask);

        _bitmasks[_tokenId] = newMask;

        emit BitMaskUpdated(_tokenId, newMask);
    }

    /**
     * @dev Internal function to remove bits in `_removingMask` for gateway token with `_tokenId`
     */
    function _removeBits(uint256 _tokenId, uint8 _removingMask) internal {
        uint256 mask = _bitmasks[_tokenId];
        uint256 newMask = mask.negate();

        newMask = newMask.or(_removingMask);
        newMask = newMask.negate();
        _bitmasks[_tokenId] = newMask;

        emit BitMaskUpdated(_tokenId, newMask);
    }

    /**
     * @dev Internal function to clear one bit in particular `_index` for gateway token with `_tokenId`
     * @notice This function performs validation on supported flags on the gateway token system level
     */
    function _clearBit(uint256 _tokenId, uint8 _index) internal {
        uint256 mask = _bitmasks[_tokenId];
        uint256 newMask = mask.clearBit(_index);
        _checkSupportedBits(newMask);

        _bitmasks[_tokenId] = newMask;

        emit BitMaskUpdated(_tokenId, newMask);
    }

    /**
     * @dev Internal function to delete bitmask associated with `_tokenId`
     */
    function _clearBitMask(uint256 _tokenId) internal {
        delete _bitmasks[_tokenId];
        emit BitMaskUpdated(_tokenId, 0);
    }

    /**
     * @dev Internal function to check if gateway token bitmask contains any high risk bits using `_highRiskBitMask` mask
     * @notice Returns false if bitmask has no high risk bits, true otherwise
     */
    function _checkHighRiskBitMask(uint256 _tokenId, uint256 _highRiskBitMask)
        internal
        view
        returns (bool)
    {
        uint256 mask = _bitmasks[_tokenId];
        uint256 riskMask = mask.and(_highRiskBitMask);

        return riskMask != uint256(0);
    }

    /**
     * @dev Internal function to check if `_mask` contains only supported bits from FlagsStorage
     */
    function _checkSupportedBits(uint256 _mask) internal {
        uint256 supportedMask = flagsStorage.supportedFlagsMask();
        require(supportedMask == supportedMask.or(_mask), "UNSUPPORTED BITS");
    }

    /**
     * @dev Internal function to clear unsupported bits for gateway token bitmask with `_tokenId`
     */
    function _checkUnsupportedBits(uint256 _tokenId) internal {
        uint256 unsupportedBitMask = flagsStorage.unsupportedFlagsMask();
        uint256 mask = _bitmasks[_tokenId];
        uint256 targetBits = mask.and(unsupportedBitMask);

        if (targetBits != uint256(0)) {
            mask = mask.negate();
            mask = mask.or(targetBits);
            mask = mask.negate();

            _bitmasks[_tokenId] = mask;
            emit BitMaskUpdated(_tokenId, mask);
        }
    }
}

contracts/GatewayToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/IERC721Metadata.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import "./TokenBitMask.sol";
import "./interfaces/IERC721Freezeble.sol";
import "./interfaces/IGatewayToken.sol";
import "./interfaces/IGatewayTokenController.sol";
import "./interfaces/IERC721Expirable.sol";
import "./interfaces/IERC721Revokable.sol";
import "./MultiERC2771Context.sol";
import "./library/Charge.sol";


/**
 * @dev Gateway Token contract is responsible for managing Identity.com KYC gateway tokens 
 * those tokens represent completed KYC with attached identity. 
 * Gateway tokens using ERC721 standard with custom extentions.
 *
 * Contract handles multiple levels of access such as Network Authority (may represent a specific regulator body) 
 * Gatekeepers (Identity.com network parties who can mint/burn/freeze gateway tokens) and overall system Admin who can add
 * new Gatekeepers and Network Authorities
 */
contract GatewayToken is MultiERC2771Context, ERC165, AccessControl, IERC721, IERC721Metadata, IERC721Freezeble, IERC721Expirable, IERC721Revokable, IGatewayToken, TokenBitMask {
    using Address for address;
    using Strings for uint256;

    enum TokenState {
        ACTIVE, FROZEN, REVOKED
    }

    // Gateway Token name
    string public override name;

    // Gateway Token symbol
    string public override symbol;

    // Gateway Token controller contract address
    address public controller;
    address public deployer;

    // Gateway token transfer restrictions
    bool public isTransfersRestricted;

    // Off-chain DAO governance access control
    bool public override isDAOGoverned;
    address public override daoManager;

    // Access control roles
    bytes32 public constant DAO_MANAGER_ROLE = keccak256("DAO_MANAGER_ROLE");
    bytes32 public constant GATEKEEPER_ROLE = keccak256("GATEKEEPER_ROLE");
    bytes32 public constant NETWORK_AUTHORITY_ROLE = keccak256("NETWORK_AUTHORITY_ROLE");

    // Mapping from token ID to owner address
    mapping(uint256 => address) private _owners;

    // Mapping owner address to token count
    mapping(address => uint256) private _balances;

    // Mapping from token ID to approved address
    mapping(uint256 => address) private _tokenApprovals;

    // Mapping from owner to operator approvals
    mapping (address => mapping (address => bool)) private _operatorApprovals;

    // Optional mapping for gateway token Identities (via TokenURI)
    mapping(uint256 => string) private _tokenURIs;

    // Optional mapping for gateway token bitmaps
    mapping(uint256 => TokenState) private _tokenStates;

    // Optional Mapping from token ID to expiration date
    mapping(uint256 => uint256) private _expirations;

    // Optional Mapping from address to tokenID
    mapping(address => uint256) private _defaultTokens;

    // @dev Modifier to prevent calls from anyone except Identity.com Admin
    modifier onlyIdentityAdmin() {
        require(msg.sender == IGatewayTokenController(controller).identityAdmin() || msg.sender == controller, "NOT IDENTITY.COM ADMIN NOR TOKEN CONTROLLER");
        _;
    }

    // @dev Modifier to prevent calls for blacklisted users
    modifier onlyNonBlacklistedUser(address user) {
        require(!_isBlacklisted(user), "BLACKLISTED USER");
        _;
    }

    // @dev Modifier to make a function callable only when token transfers not restricted.
    modifier whenTransfersNotRestricted() {
        require(!transfersRestricted(), "TRANSFERS RESTRICTED");
        _;
    }

    // @dev Modifier to make a function callable only when token transfers restricted.
    modifier whenTransfersRestricted() {
        require(transfersRestricted(), "TRANSFERS NOT RESTRICTED");
        _;
    }

    /**
     * @dev Gateway Token constructor initializes the contract by 
     * setting a `name` and a `symbol` to the gateway token.
     *
     * Initiates gateway token roles with main system admin `GATEWAY_TOKEN_CONTROLLER`,
     * `NETWORK_AUTHORITY_ROLE` responsible for adding/removing Gatekeepers and 
     * `GATEKEEPER_ROLE` responsible for minting/burning/transferring tokens
     */
    constructor(string memory _name, string memory _symbol, address _deployer, bool _isDAOGoverned, address _daoManager, address _flagsStorage, address[] memory trustedForwarders) MultiERC2771Context(trustedForwarders) public {
        name = _name;
        symbol = _symbol;
        controller = _msgSender();
        isTransfersRestricted = true;
        deployer = _deployer;
        _setFlagsStorage(_flagsStorage);

        _setupRole(NETWORK_AUTHORITY_ROLE, _msgSender());
        _setupRole(NETWORK_AUTHORITY_ROLE, deployer);
        _setupRole(GATEKEEPER_ROLE, deployer);

        if (_isDAOGoverned) {
            isDAOGoverned = _isDAOGoverned;

            require(_daoManager != address(0), "INCORRECT ADDRESS");
            // require(_daoManager.isContract(), "NON CONTRACT EXECUTOR"); uncomment while testing with Gnosis Multisig
            daoManager = _daoManager;

            _setupRole(DAO_MANAGER_ROLE, _daoManager);
            _setupRole(DAO_MANAGER_ROLE, _msgSender());
            _setupRole(NETWORK_AUTHORITY_ROLE, _daoManager);
            _setupRole(GATEKEEPER_ROLE, _daoManager);
            _setRoleAdmin(NETWORK_AUTHORITY_ROLE, DAO_MANAGER_ROLE);
            _setRoleAdmin(GATEKEEPER_ROLE, DAO_MANAGER_ROLE);
        } else {
            _setRoleAdmin(NETWORK_AUTHORITY_ROLE, NETWORK_AUTHORITY_ROLE);
            _setRoleAdmin(GATEKEEPER_ROLE, NETWORK_AUTHORITY_ROLE);
        }
    }

    function _msgSender() internal view virtual override(MultiERC2771Context, Context) returns (address sender) {
        return MultiERC2771Context._msgSender();
    }

    function _msgData() internal view virtual override(MultiERC2771Context, Context) returns (bytes calldata) {
        return MultiERC2771Context._msgData();
    }

    function addForwarder(address forwarder) public override(MultiERC2771Context) onlyIdentityAdmin {
        super.addForwarder(forwarder);
    }

    function removeForwarder(address forwarder) public override(MultiERC2771Context) onlyIdentityAdmin {
        super.removeForwarder(forwarder);
    }

    /**
     * @dev Returns true if gateway token owner transfers reestricted, and false otherwise.
     */
    function transfersRestricted() public view virtual returns (bool) {
        return isTransfersRestricted;
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC165, IERC165, AccessControl) returns (bool) {
        return
            interfaceId == type(IERC721).interfaceId ||
            interfaceId == type(IERC721Metadata).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**
    * @dev Triggers to check total amount of gateway tokens hold by specific address. 
    * @param owner Token owner address
    */
    function balanceOf(address owner) public view virtual override returns (uint256) {
        require(owner != address(0), "ZERO ADDRESS");
        return _balances[owner];
    }

    /**
    * @dev Triggers to get owner for specific gateway token
    * @param tokenId Gateway token id
    */
    function ownerOf(uint256 tokenId) public view virtual override returns (address) {
        address owner = _owners[tokenId];
        require(owner != address(0), "ZERO ADDRESS");
        return owner;
    }

    /**
    * @dev Triggers to get identity attached to specific gateway token
    * @param tokenId Gateway token id
    */
    function getIdentity(uint256 tokenId) public view virtual returns (string memory) {
        return tokenURI(tokenId);
    }

    /**
    * @dev Triggers to get tokenURI attached to specificied `tokenId`
    * @param tokenId Gateway token id
    */
    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "TOKEN DOESN'T EXIST OR FROZEN");
        string memory _tokenURI = _tokenURIs[tokenId];

        return _tokenURI;
    }

    /**
    * @dev Triggers to attach identity string to specific gateway token
    * @param tokenId Gateway token id
    * @param tokenURI Gateway token identity in a form of TokenURI
    *
    * @notice Only executable by gatekeepers
    */
    function setTokenURI(uint256 tokenId, string memory tokenURI) public virtual {
        require(hasRole(GATEKEEPER_ROLE, _msgSender()), "MUST BE GATEKEEPER");
        require(_existsAndActive(tokenId), "TOKEN DOESN'T EXIST OR FROZEN");
        address tokenOwner = ownerOf(tokenId);
        require(!_isBlacklisted(tokenOwner), "BLACKLISTED USER");

        _tokenURIs[tokenId] = tokenURI;
    }

    /**
    * @dev Triggered by external contract to verify if `tokenId` and token `owner` are correct.
    *
    * Checks if token exists in gateway token contract, `tokenId` still active, and not expired.
    * Performs additional checks to verify that `owner` is not blacklisted globally.
    */
    function verifyToken(address owner, uint256 tokenId) external view virtual returns (bool) {
        if(!_existsAndActive(tokenId)) return false;
        address tokenOwner = ownerOf(tokenId);
        if (tokenOwner != owner) return false;
        bool _blacklisted = _isBlacklisted(owner);
        if(_blacklisted) return false;

        return true;
    }

    /**
    * @dev Triggered by external contract to verify the validity of the default token for `owner`.
    *
    * Checks owner has any token on gateway token contract, `tokenId` still active, and not expired.
    * Performs additional checks to verify that `owner` is not blacklisted globally.
    */
    function verifyToken(address owner) external view virtual returns (bool) {
        uint256 tokenId = getTokenId(owner);
        if(!_existsAndActive(tokenId)) return false;
        address tokenOwner = ownerOf(tokenId);
        if (tokenOwner != owner) return false;
        bool _blacklisted = _isBlacklisted(owner);
        if(_blacklisted) return false;

        return true;
    }

    /**
    * @dev Triggers to get all information gateway token related to specified `tokenId`
    * @param tokenId Gateway token id
    */
    function getToken(uint256 tokenId) public view virtual override
        returns (
            address owner,
            uint8 state,
            string memory identity,
            uint256 expiration,
            uint256 bitmask
        ) 
    {
        owner = ownerOf(tokenId);
        state = uint8(_tokenStates[tokenId]);
        identity = _tokenURIs[tokenId];
        expiration = _expirations[tokenId];
        bitmask = _getBitMask(tokenId);

        return (owner, state, identity, expiration, bitmask);
    }

    /**
    * @dev Triggers to get gateway token state with specified `tokenId`
    * @param tokenId Gateway token id
    */
    function getTokenState(uint256 tokenId) public view virtual override returns (uint8 state) {
        state = uint8(_tokenStates[tokenId]);

        return state;
    }

    /**
    * @dev Returns whether `tokenId` exists.
    *
    * Tokens start existing when they are minted (`_mint`),
    * pause when they are freezed (`_freeze`), and 
    * stop existing when they are burned (`_burn`).
    */
    function _exists(uint256 tokenId) internal view virtual returns (bool) {
        return _owners[tokenId] != address(0);
    }

    /**
    * @dev Returns whether `tokenId` exists and not freezed.
    */
    function _existsAndActive(uint256 tokenId) internal view virtual returns (bool) {
        if (_expirations[tokenId] != 0) {
            return _owners[tokenId] != address(0) && _tokenStates[tokenId] == TokenState.ACTIVE && block.timestamp <= _expirations[tokenId];
        } else {
            return _owners[tokenId] != address(0) && _tokenStates[tokenId] == TokenState.ACTIVE;
        }
    }

    /**
     * @dev Returns whether `spender` is allowed to manage `tokenId`.
     *
     * Requirements:
     *
     * - `tokenId` must exist.
     */
    function _isApprovedOrOwner(address spender, uint256 tokenId) internal view virtual returns (bool) {
        require(_exists(tokenId), "ERC721: operator query for nonexistent token");
        address owner = ownerOf(tokenId);
        if (spender == owner) {
            return !transfersRestricted();
        } else {
            return (getApproved(tokenId) == spender || isApprovedForAll(owner, spender) || hasRole(GATEKEEPER_ROLE, spender));
        }
    }

    /**
    * @dev Triggers to request token ownership transfer
    * @param to Address to transfer token ownership
    * @param tokenId Gateway token id
    *
    * @notice Only executable by token owner
    */
    function approve(address to, uint256 tokenId) public virtual override {
        address owner = GatewayToken.ownerOf(tokenId);
